    - uses: AdoptOpenJDK/install-jdk@v1
      if: matrix.sdk_resource == 'customized'
      with:
        version: ${{ matrix.jdk_version }}
        source: ${{ matrix.customized_sdk_url }}
        archiveExtension: ${{ matrix.archive_extension }}
        sourceType: 'url'
        impl: ${{ matrix.jdk_impl }}
