    public String getTestFlag() {
        String testFlag = "";
        checkCRIU();
        checkVTstandard();
        checkJFR();
        String envTf = System.getenv("TEST_FLAG");
        String paddedTf = null;
        if (envTf != null && !envTf.isEmpty()) {
            testFlag = envTf;
            paddedTf =  "," + envTf + ",";
        }
        for (String dtf : detectedTfs) {
            if ((paddedTf == null) || !paddedTf.contains("," + dtf + ",")) {
