		this.md = md;
		this.tt = tt;
	}

	@Override
	public boolean visit(File playlistXML, String absoluteDir, List<String> dirList, List<String> subDirs, List<String> ignoreOnRerunList) {
		PlaylistInfoParser parser = new PlaylistInfoParser(arg, md, tt, playlistXML);
		parser.parse();
		return true;
	}
}

