        if (output && output.length > size) {
            output = output.substr(-size);
        }
        if (id) {
            const result = await outputDB.update(
                { _id: new ObjectID(id) },
                { $set: { output } }
            );
            return id;
        } else {
            const status = await outputDB.populateDB({ output });
            if (status && status.insertedCount === 1) {
