        consoleOutput2 = await jenkinsInfo.getBuildOutput(
            url2,
            buildName2,
            buildNum2
        );
    } catch (error) {
        throw 'Failed to obtain Jenkins Outputs!';
    }

    if (!consoleOutput1 || !consoleOutput2) {
        throw 'Jenkins Output is empty!';
