    const {
        url,
        buildName,
        buildNum,
        testName,
        removeTimestampFlag,
        applyDeepSmithMatchFlag,
    } = req.query;
    const testResultsDB = new TestResultsDB();
    const outputDB = new OutputDB();
    const data = await testResultsDB.aggregate([
        {
            $match: {
                url: url,
                buildName: buildName,
                buildNum: parseInt(buildNum, 10),
            },
        },
        { $unwind: '$tests' },
        {
            $match: {
                'tests.testName': testName,
            },
        },
        {
            $project: {
                tests: 1,
            },
        },
    ]);
