searchOutputId = async (build) => {
    const testResultsDB = new TestResultsDB();
    let rt = { buildOutputIds: {}, testOutputIds: {} };
    if (build.buildOutputId) {
        rt.buildOutputIds[build.buildOutputId] = {
            _id: build._id,
            buildName: build.buildName,
            buildNum: build.buildNum,
            buildResult: build.buildResult,
            buildUrl: build.buildUrl,
            type: build.type,
            hasChildren: build.hasChildren,
            timestamp: build.timestamp,
            url: build.url,
            testSummary: build.testSummary,
        };
    }
    if (!build.hasChildren) {
        if (build.tests) {
            build.tests.forEach((test) => {
                rt.testOutputIds[test.testOutputId] = {
                    _id: test._id,
                    buildName: build.buildName,
                    buildNum: build.buildNum,
                    buildUrl: build.buildUrl,
                    testName: test.testName,
                    testResult: test.testResult,
                    duration: test.duration,
                    machine: build.machine,
                    timestamp: build.timestamp,
                };
            });
        }
    } else {
        let builds = await testResultsDB.getSpecificData(
            { parentId: new ObjectID(build._id) },
            {
                _id: 1,
                buildName: 1,
                buildNum: 1,
                buildResult: 1,
                buildUrl: 1,
                type: 1,
                hasChildren: 1,
                tests: 1,
                buildOutputId: 1,
                timestamp: 1,
                url: 1,
                testSummary: 1,
                machine: 1,
            }
        );
        await Promise.all(
            builds.map(async (b) => {
                let res = await searchOutputId(b);
                if (res.testOutputIds) {
                    rt.testOutputIds = Object.assign(
                        {},
                        rt.testOutputIds,
                        res.testOutputIds
                    );
                }
                if (res.buildOutputIds) {
                    rt.buildOutputIds = Object.assign(
                        {},
                        rt.buildOutputIds,
                        res.buildOutputIds
                    );
                }
                return b;
            })
        );
    }
    return rt;
};

