def interact(title: str, description: str, model: str, previousComments: list, userComment: str):
    if model == "chatgpt":
        response = OpenAI_client.chat.completions.create(
            model="gpt-3.5-turbo",
            messages=[
                {
                    "role": "system",
                    "content": generate_interact_prompt(title, description)
                }, 
                *previousComments,
                {
                    "role": "user",
                    "content": userComment
                }
            ],
            temperature=1,
            top_p=1,
            frequency_penalty=0,
            presence_penalty=0
        )
        return {"response": response.choices[0].message.content}

    elif model == "gemini":
        response = Gemini_client.generate_content([
            {
                "role": "user",
                "parts": [generate_interact_prompt(title, description)]
            },
            *previousComments,
            {
                "role": "user",
                "parts": [userComment]
            }
        ])
        return {"response": response.text}

    elif model == "llama3":
        response = Llama_client.chat_completion(
            messages=[
                {
                    "role": "system",
                    "content": generate_interact_prompt(title, description)
                }, 
                *previousComments,
                {
                    "role": "user",
                    "content": userComment
                }
            ],
        )

        return {"response": response.choices[0].message.content}

if __name__ == '__main__':
    uvicorn.run('main:app', host='0.0.0.0', port=3000)

