        },
        {
            title: 'Test Result',
            dataIndex: 'testResult',
            key: 'testResult',
            render: renderTestResult,
        },
        {
            title: 'Duration',
            dataIndex: 'duration',
            key: 'duration',
            render: renderDuration,
            sorter: (a, b) => {
                return a.duration - b.duration;
            },
            sortOrder: sortedInfo.columnKey === 'duration' && sortedInfo.order,
        },
        {
            title: 'Machine',
            dataIndex: 'machine',
            key: 'machine',
            filterDropdown: (
                <div className="custom-filter-dropdown">
                    <Input
                        ref={(ele) => (this.searchInput = ele)}
                        placeholder="Search machine name"
                        value={searchText}
                        onChange={onInputChange}
                        onPressEnter={onSearch}
                    />
                </div>
            ),
            filterDropdownVisible,
            onFilterDropdownOpenChange: (visible) => {
                setFilterDropdownVisible(visible);
            },
        },
        {
            title: 'Start Time',
            dataIndex: 'startTime',
            key: 'startTime',
            sorter: (a, b) => {
                return a.startTime.localeCompare(b.startTime);
            },
            sortOrder: sortedInfo.columnKey === 'startTime' && sortedInfo.order,
        },
        {
            title: 'Action',
            dataIndex: 'action',
            key: 'action',
            render: renderAction,
        },
    ];

    if (searchText) {
        const reg = new RegExp(searchText, 'gi');
        dataSource = dataSource
            .filter((record) => !!record.machine.match(reg))
            .map((record) => {
                return {
                    ...record,
                    machine: (
                        <Highlighter
                            searchWords={searchText.split(' ')}
                            autoEscape
                            textToHighlight={record.machine}
                        />
                    ),
                };
            });
    }

    return (
        <div>
            <TestBreadcrumb
                buildId={build._id}
                testId={testId}
                testName={testName}
            />
            <Table
                columns={columns}
                dataSource={dataSource}
                bordered
                title={() => testName}
                pagination={{ pageSize: 50 }}
                onChange={handleChange}
                defaultSortOrder="descend"
                defaultSortField="startTime"
            />
        </div>
    );
};

export default DeepHistory;

