                                    ': ' +
                                    Highcharts.numberFormat(
                                        this.point.percentage,
                                        2
                                    ) +
                                    ' %'
                                );
                            }
                        },
                    },
                    colors,
                    shadow: true,
                    showInLegend,
                },
            },
            series: [
                {
                    events: {
                        click: (e) => {
                            this.categoryClicked(e);
                        },
                    },
                    name: 'Test Groups',
                    colorByPoint: true,
                    data: pieChartData,
                },
            ],
            drilldown: {
                series: drilldownSeries,
            },
        };
        return (
            <div className="chart">
                <HighchartsReact
                    allowChartUpdate={this.allowChartUpdate}
                    highcharts={Highcharts}
                    options={options}
                />
            </div>
        );
    }
}

