const renderBuild = (value, row, index) => {
        return (
            <div>
                <Link
                    to={{
                        pathname: '/output/test',
                        search: params({ id: value.testId }),
                    }}
                    style={{
                        color:
                            value.testResult === 'PASSED'
                                ? '#2cbe4e'
