const testResultSortingArray = [
    'FAILED',
    'PASSED',
    'DISABLED',
    'SKIPPED',
    'N/A',
];

export default class TestTable extends Component {
    state = {
        filteredData: undefined,
    };

    handleFilterChange = (filteredData) => {
        this.setState({ filteredData });
    };

