const renderTotals = (value, row, index) => {
            // Return 'N/A' if no value
            if (!value) return <div>N/A</div>;
            // Destructure the value object
            const {
                failed = 0,
                passed = 0,
                disabled = 0,
                skipped = 0,
                total = 0,
            } = value;
            // Get the build result and ID
            const buildResult = row.build.buildResult;
            const id = row.build._id;
            // Return JSX
            return (
                <>
                    <Link
                        to={{
                            pathname: '/resultSummary',
                            search: params({ parentId: id }),
                        }}
                        style={{
                            color:
                                buildResult === 'SUCCESS'
                                    ? '#2cbe4e'
                                    : buildResult === 'FAILURE'
                                    ? '#f50'
                                    : '#DAA520',
                        }}
                    >
                        Grid
                    </Link>
                    <div>
                        <BuildLink
                            id={id}
                            label="Failed: "
                            link={failed}
                            testSummaryResult="failed"
                            buildNameRegex="^Test.*"
                        />
                        <BuildLink
                            id={id}
                            label="Passed: "
                            link={passed}
                            testSummaryResult="passed"
                            buildNameRegex="^Test.*"
                        />
                        <BuildLink
                            id={id}
                            label="Disabled: "
                            link={disabled}
                            testSummaryResult="disabled"
                            buildNameRegex="^Test.*"
                        />
                        <BuildLink
                            id={id}
                            label="Skipped: "
                            link={skipped}
                            testSummaryResult="skipped"
                            buildNameRegex="^Test.*"
                        />
                        <BuildLink
                            id={id}
                            label="Total: "
                            link={total}
                            testSummaryResult="total"
                            buildNameRegex="^Test.*"
                        />
                    </div>
                </>
            );
        };

        const renderBuildResults = (value) => {
            return (
                <div>
                    <BuildLink
                        id={value._id}
                        link="Failed Builds "
                        buildResult="!SUCCESS"
                    />
                </div>
            );
        };

        const renderPublishName = ({ buildParams = [] }) => {
            if (buildParams) {
                const param = buildParams.find(
                    (param) => param.name === 'overridePublishName'
                );
                if (param) return param.value;
            }
            return;
        };

