    static Title = (props) => 'Dacapo';
    static defaultSize = { w: 2, h: 4 };
    static Setting = (<Settings servers={servers} builds={builds} />);

    state = {
        displaySeries: [],
    };

    async componentDidMount() {
        await this.updateData();
    }

    async componentDidUpdate(prevProps) {
        if (
            prevProps.buildSelected !== this.props.buildSelected ||
            prevProps.serverSelected !== this.props.serverSelected
        ) {
            await this.updateData();
        }
    }

    async updateData() {
        const { buildSelected, serverSelected } = this.props;
        const buildName = encodeURIComponent(buildSelected);
        let results = await fetchData(
            `/api/getBuildHistory?type=Perf&buildName=${buildName}&status=Done&limit=100&asc`
        );
        const buildInfoMap = await fetchData(`/api/getDashboardBuildInfo`);

        if (serverSelected) {
            if (serverSelected === 'AdoptOpenJDK') {
                results = results.filter((result) =>
                    result.buildUrl.includes(buildInfoMap['AdoptOpenJDK'].url)
                );
            } else {
                results = results.filter(
                    (result) =>
                        !result.buildUrl.includes(
                            buildInfoMap['AdoptOpenJDK'].url
                        )
                );
            }
        }

        const resultsByJDKBuild = {};

        // combine results having the same JDK build date
        results.forEach((t, i) => {
            //t.jdkDate is currently not in use
            let jdkDate = (t.timestamp ? new Date(t.timestamp).toISOString().slice(0,10) : null);
            if (t.buildResult !== 'SUCCESS' || !jdkDate) return;
            jdkDate = ' ' + jdkDate.replaceAll('-', '');
            resultsByJDKBuild[jdkDate] = resultsByJDKBuild[jdkDate] || [];
            t.tests.forEach((test, i) => {
                let eclipse = null;
                let h2 = null;
                let lusearch = null;

                if (test.benchmarkName === 'dacapo-eclipse') {
                    eclipse = test.duration
                }

                if (test.benchmarkName === 'dacapo-h2') {
                    h2 = test.duration
                }
                if (test.benchmarkName === 'dacapo-lusearch-fix') {
                    lusearch = test.duration 
                }

                if (!eclipse && !h2 && !lusearch) {
                    return;
                }
                // TODO: current code only considers one interation. This needs to be updated
                resultsByJDKBuild[jdkDate].push({
                    eclipse,
                    h2,
                    lusearch,
                    additionalData: {
                        testId: test._id,
                        buildName: t.buildName,
                        buildNum: t.buildNum,
                        javaVersion: t.javaVersion,
                        jdkDate: jdkDate,
                    },
                });
            });
        });

        const [eclipseData, eclipseStd, eclipseMean, eclipseMedian] =
            getStatisticValues(resultsByJDKBuild, 'eclipse');
        const [h2Data, h2Std, h2Mean, h2Median] = getStatisticValues(
            resultsByJDKBuild,
            'h2'
        );
        const [lusearchData, lusearchStd, lusearchMean, lusearchMedian] =
            getStatisticValues(resultsByJDKBuild, 'lusearch');

