const queryForParseJenkinsUrl =
                '/api/parseJenkinsUrl?jenkinsUrl=' +
                buildUrl +
                '&compareType=' +
                compareType;
            const parseRes = await fetchData(queryForParseJenkinsUrl);
            if (parseRes && parseRes.output) {
                const { errorMsg, serverUrl, buildName, buildNum } =
                    parseRes.output;
                if (!errorMsg) {
                    let queryForGetOutput = '';
                    if (testName) {
                        queryForGetOutput =
                            '/api/getOutputByTestInfo?url=' +
                            encodeURIComponent(serverUrl) +
                            '&buildName=' +
                            encodeURIComponent(buildName) +
                            '&buildNum=' +
                            buildNum +
                            '&testName=' +
                            testName +
                            '&removeTimestampFlag=' +
                            removeTimestampFlag +
                            '&applyDeepSmithMatchFlag=' +
                            applyDeepSmithMatchFlag;
                    } else {
                        const initialQueryForBuildID =
                            '/api/getTestInfoByBuildInfo?url=' +
                            serverUrl +
                            '&buildName=' +
                            buildName +
                            '&buildNum=' +
                            buildNum;
                        const queryIDRes = await fetchData(
                            initialQueryForBuildID
                        );
                        if (
                            !(
                                queryIDRes &&
                                queryIDRes.testInfo &&
                                queryIDRes.testInfo[0].buildOutputId
                            )
                        ) {
                            alert(
                                'Cannot find data with provided ' +
                                    compareType +
                                    ' Build URL:\n' +
                                    buildUrl
                            );
                            return;
                        }
                        queryForGetOutput =
                            '/api/getOutputById?id=' +
                            queryIDRes.testInfo[0].buildOutputId +
                            '&removeTimestampFlag=' +
                            removeTimestampFlag +
                            '&applyDeepSmithMatchFlag=' +
                            applyDeepSmithMatchFlag;
                    }
                    const res = await fetchData(queryForGetOutput);
                    if (res && res.output) {
                        this.state.tests[i] = res;
                    } else {
                        const testNameAlertMsg = testName
                            ? ' and Test Name'
                            : '';
                        alert(
                            'Cannot find data with provided ' +
                                compareType +
                                ' Build URL' +
                                testNameAlertMsg +
                                ':\n' +
                                buildUrl +
                                '\n' +
                                testName
                        );
                        return;
                    }
                } else {
                    alert(errorMsg);
                    return;
                }
            } else {
                alert('Failed to connect with API to parse Jenkins URL!');
                return;
            }
        }
        this.setState(this.state);

        $(this.compare).mergely('lhs', this.state.tests[0].output);
        $(this.compare).mergely('rhs', this.state.tests[1].output);
    };

