import { Link } from 'react-router-dom';
import { params } from '../utils/query';
import { fetchData } from '../utils/Utils';
import { getColumnSearchProps } from '../utils/TableUtils';

export default class ThirdPartyAppView extends Component {
    constructor(props) {
        super(props);
        this.allJDKVersions = [];
        this.allJDKImpls = [];
        this.state = {
            appTests: [],
            searchText: '',
            searchedColumn: '',
        };
    }

    async componentDidMount() {
        await this.updateData();
    }

    async updateData() {
        const appTestsData = await fetchData(`/api/getApplicationTests`);

        this.allJDKVersions = this.getDistinctValues(
            appTestsData,
            'jdkVersion'
        );

        this.allJDKImpls = this.getDistinctValues(appTestsData, 'jdkImpl');

        const appTests = appTestsData.map(
            ({
                appName,
                testName,
                testId,
                testResult,
                timestamp,
                buildName,
                buildNum,
                buildUrl,
                url,
                jdkVersion,
                javaVersionOutput,
                ...rest
            }) => ({
                ...rest,
                name: appName + '/' + testName,
                key: buildUrl,
                result: { testResult, testId },
                jdkVersion: { versionNum: jdkVersion, javaVersionOutput },
                build: { buildName, buildNum, buildUrl, url },
                date: new Date(timestamp).toLocaleString(),
            })
        );
        this.setState({ appTests });
    }

    getDistinctValues(array, key) {
        return array
            .map((element) => element[key])
            .filter((value, index, self) => self.indexOf(value) === index);
    }

    handleSearch = (selectedKeys, confirm, dataIndex) => {
        confirm();
        this.setState({
            searchText: selectedKeys[0],
            searchedColumn: dataIndex,
        });
    };

    handleReset = (clearFilters) => {
        clearFilters();
        this.setState({ searchText: '' });
    };

    render() {
        const { appTests } = this.state;
        if (appTests) {
            const renderBuildUrl = ({ buildName, buildNum, buildUrl, url }) => {
                return (
                    <div>
                        <a
                            href={buildUrl}
                            target="_blank"
                            rel="noopener noreferrer"
                        >
                            {buildName} #{buildNum}
                        </a>
                    </div>
                );
            };

            const renderResult = ({ testResult, testId }) => {
                return (
                    <div>
                        {testId ? (
                            <Link
                                to={{
                                    pathname: '/output/test',
                                    search: params({ id: testId }),
                                }}
                                style={{
                                    color:
                                        testResult === 'PASSED'
                                            ? '#2cbe4e'
                                            : testResult === 'FAILED'
                                            ? '#f50'
                                            : '#DAA520',
                                }}
                            >
                                {testResult}
                            </Link>
                        ) : (
                            testResult
                        )}
                    </div>
                );
            };

