package net.adoptopenjdk.bumblebench.collections;

import java.util.ArrayList;
import java.util.function.Consumer;

public final class ArrayListForEachConsumerAnonymousBench extends CollectionsBench {
	private final ArrayList<Integer> _arrayList = newArrayList();
	private long _sum;

	protected long doBatch(long numIterations) throws InterruptedException {
		for (long i = 0; i < numIterations; i+=1) {
			_sum = 0;
			_arrayList.forEach(new Consumer<Integer>() {
				@Override
				public void accept(Integer v) {
					_sum += v;
				}
			});
		}
		return numIterations;
	}

}

