	protected ArrayList<Integer> newArrayList() {
		ArrayList<Integer> result = new ArrayList<Integer>(ARRAY_SIZE);
		for(int i = 0; i < ARRAY_SIZE; i++){
			result.add(new Integer(i));
		}
		return result;
	}

