		HashMap<Integer, Integer> result = new HashMap<Integer, Integer>(HASH_MAP_SIZE);
		for(int i = 0; i < HASH_MAP_SIZE; i++){
			result.put(new Integer(i), new Integer(i));
		}
		return result;
	}
}

