		mv.visitVarInsn(ALOAD, 0);
		mv.visitMethodInsn(INVOKESPECIAL, Super, "<init>", "()V");
		mv.visitInsn(RETURN);

		mv.visitMaxs(1, 0);
		mv.visitEnd();
		}

		cw.visitEnd();
		return loadClass(className, cw.toByteArray());
	}

